//6x6

let x = [
  [, , , , ,],
  [, , , , ,],
  [, , , , ,],
  [, , , , ,],
  [, , , , ,],
  [, , , , ,]
];

let red;
let yellow;

for (let i = 0; i < x.length; i++) {
  if (x[i] != undefined) {
    if (x[i[0]] === 0) {
      yellow += 1;
      if (x[i[1]] === 0) {
        yellow += 1;
        if (x[i[2]] === 0) {
          yellow += 1;
          if (x[i[3]] === 0) {
