import React from "react";
import "./Home.scss";
import Guess from "./../guess/Guess";

import Hm1 from "../../Assets/hangman1.png";
import Hm2 from "../../Assets/hangman2.png";
import Hm3 from "../../Assets/hangman3.png";
import Hm4 from "../../Assets/hangman4.png";
import Hm5 from "../../Assets/hangman5.png";
import Hm6 from "../../Assets/hangman6.png";
import Hm7 from "../../Assets/hangman8.png";
import Hm8 from "../../Assets/hangman9.png";

let hangman = Hm3;
let guessArr = [];
let correctArr = [];
let error = 0;
let anything = false;

let newWord = wrd => {
  guessArr = [];
  for (let i = 0; i < wrd.length; i++) {
    correctArr[i] = wrd[i];
    guessArr[i] = "_";
  }
};

const alphabet = [
  "a",
  "b",
  "c",
  "d",
  "e",
  "f",
  "g",
  "h",
  "i",
  "j",
  "k",
  "l",
  "m",
  "n",
  "o",
  "p",
  "q",
  "r",
  "s",
  "t",
  "u",
  "v",
  "w",
  "x",
  "y",
  "z"
];

newWord("hello");
console.log(guessArr, correctArr);

export default class Home extends React.Component {
  state = { guess: guessArr, image: Hm1, ltr: alphabet };

  newGuess = e => {
    //Here we can use this to update the website information.
    e.preventDefault();
    anything = false;
    if (e.target.value !== "" || undefined || null) {
      for (let i = 0; i < correctArr.length; i++) {
        if (e.target.value === correctArr[i]) {
          guessArr[i] = correctArr[i];
          anything = true;
          this.setState({
            guess: guessArr
          });
        }
      }
      if (!anything) {
        error += 1;
        this.setState({
          ltr: (alphabet[alphabet.indexOf(e.target.value)] = `<strike>${
            alphabet[alphabet.indexOf(e.target.value)]
          }</strike>`)
        });
        console.log(this.state.ltr);

        if (error === 1) {
          hangman = Hm2;
        } else if (error === 2) {
          hangman = Hm3;
        } else if (error === 3) {
          console.log(error);
          hangman = Hm4;
        } else if (error === 4) {
          hangman = Hm5;
        } else if (error === 5) {
          hangman = Hm6;
        } else if (error === 6) {
          hangman = Hm7;
        }
        this.setState({
          image: hangman
        });
      }

      console.log(e.target.value);
      console.log(correctArr);
      if (this.state.guess.toString() === correctArr.toString()) {
        this.setState({
          image: Hm8
        });
      }
      console.log(this.state.guess);
    }
  };

  // componentDidUpdate() {}

  render() {
    // console.log(this.state.guess);

    return (
      <form onSubmit={this.newGuess}>
        <div class="segment">
          <h1>Hangman</h1>
          <h2>USED LETTERS</h2>
          <h3>{this.state.ltr.toString}</h3>
        </div>
        <img className="hangman__img" src={this.state.image} alt="" />
        <div className="hangman__input">
          <h2 className="guess">{this.state.guess.toString()} </h2>
          {/* <h2 className="guess">{guessArr.toString()}</h2> */}
          <div>
            <label>
              <input
                type="text"
                placeholder="Guess"
                maxlength="1"
                required
                onChange={this.newGuess}
                className="hangman__text"
              />
            </label>
            {/* <span>_ _ _ _ _ _</span> */}
          </div>
          <button type="submit" class="hangman__btn">
            Submit
          </button>
        </div>
      </form>
    );
  }
}
